+import * as Sentry from '@sentry/nextjs';
+import { Button } from './ui/button'; // Assuming you have a Button component in your UI library
+
+interface SentryFeedbackButtonProps {
+  variant?: 'default' | 'destructive' | 'outline' | 'secondary' | 'ghost' | 'link';
+  size?: 'default' | 'sm' | 'lg' | 'icon';
+  className?: string;
+}
+
+/**
+ * A button component that opens the Sentry feedback dialog when clicked.
+ * This is an alternative to the automatic feedback button that appears in the corner of the page.
+ */
+export default function SentryFeedbackButton({
+  variant = 'default',
+  size = 'default',
+  className,
+}: SentryFeedbackButtonProps) {
+  const handleOpenFeedback = () => {
+    // This will open the Sentry feedback dialog
+    Sentry.showFeedbackDialog();
+    
+    // Alternatively, you can capture feedback directly with custom data
+    // Sentry.captureFeedback({
+    //   name: "User's name", // Optional
+    //   email: "user@example.com", // Optional
+    //   message: "This is required feedback message",
+    // });
+  };
+
+  return (
+    <Button 
+      variant={variant} 
+      size={size} 
+      className={className}
+      onClick={handleOpenFeedback}
+    >
+      Send Feedback
+    </Button>
+  );
+}

