import {Ping} from "@/components/Ping";
import {client} from "@/sanity/lib/client";
import {STARTUP_VIEW_BY_ID} from "@/sanity/lib/queries";


const View = async ({id}:{id:string}) => {

    const {views:totalviews} = await client.withConfig({useCdn:false}).fetch(STARTUP_VIEW_BY_ID,{id});

    {//todo : add views to the database   //todo : set view or views based on totalviews value}}



    return (
        <div className="view-container">
            <div className="-top-2 absolute -right-2">
                <Ping/>
            </div>

            <p className="view-text">
                <span className="font-black">

                       {totalviews} Views
                </span>
            </p>
        </div>
    )
}
export default View

